318   template <class _CharT, class _Alloc>
319     inline std::size_t
320     rope<_CharT, _Alloc>::
321     _S_char_ptr_len(const _CharT* __s)
322     {
323       const _CharT* __p = __s;
324       
325       while (!_S_is0(*__p))
326     ++__p;
327       return (__p - __s);
328     }
