246   template<typename _Tp>
247     _Tp 
248     __round_up_to_pow2(_Tp __x)
249     {
250       if (__x <= 1)
251     return 1;
252       else
253     return (_Tp)1 << (__rd_log2(__x - 1) + 1);
254     }
