73 // [alg.foreach]
74 // for_each_n with no policy
75 
76 template <class _ForwardIterator, class _Size, class _Function>
77 _ForwardIterator
78 __for_each_n_it_serial(_ForwardIterator __first, _Size __n, _Function __f)
79 {
80     for (; __n > 0; ++__first, --__n)
81         __f(__first);
82     return __first;
83 }
