194 _RandomAccessIterator
195 __pattern_walk_brick_n(_ExecutionPolicy&& __exec, _RandomAccessIterator __first, _Size __n, _Brick __brick,
196                        /*is_parallel=*/std::true_type)
197 {
198     return __internal::__except_handler([&]() {
