249 __pattern_walk2(_ExecutionPolicy&&, _ForwardIterator1 __first1, _ForwardIterator1 __last1, _ForwardIterator2 __first2,
250                 _Function __f, _IsVector __is_vector, /*parallel=*/std::false_type) noexcept
