109 template <class _ExecutionPolicy, typename _RandomAccessIterator1, typename _RandomAccessIterator2,
110           typename _RandomAccessIterator3, typename _Compare, typename _LeafMerge>
111 void
112 __parallel_merge(_ExecutionPolicy&&, _RandomAccessIterator1 __first1, _RandomAccessIterator1 __last1,
113                  _RandomAccessIterator2 __first2, _RandomAccessIterator2 __last2, _RandomAccessIterator3 __outit,
114                  _Compare __comp, _LeafMerge __leaf_merge)
115 {
116     __leaf_merge(__first1, __last1, __first2, __last2, __outit, __comp);
117 }
