119 template <class _ExecutionPolicy, typename _F1, typename _F2>
120 void
121 __parallel_invoke(_ExecutionPolicy&&, _F1&& __f1, _F2&& __f2)
122 {
123     std::forward<_F1>(__f1)();
124     std::forward<_F2>(__f2)();
125 }
