303 // Win32-specific per-window data
304 //
305 typedef struct _GLFWwindowWin32
306 {
307     HWND                handle;
308     HICON               bigIcon;
309     HICON               smallIcon;
310 
311     GLFWbool            cursorTracked;
312     GLFWbool            frameAction;
313     GLFWbool            iconified;
314     GLFWbool            maximized;
315     // Whether to enable framebuffer transparency on DWM
316     GLFWbool            transparent;
317     GLFWbool            scaleToMonitor;
318     GLFWbool            keymenu;
319 
320     // The last received cursor position, regardless of source
321     int                 lastCursorPosX, lastCursorPosY;
322 
323 } _GLFWwindowWin32;
