66 #if defined(CAMERA_STANDALONE)
67     // Vector2, 2 components
68     typedef struct Vector2 {
69         float x;                // Vector x component
70         float y;                // Vector y component
71     } Vector2;
72 
73     // Vector3, 3 components
74     typedef struct Vector3 {
75         float x;                // Vector x component
76         float y;                // Vector y component
77         float z;                // Vector z component
78     } Vector3;
79 
80     // Camera type, defines a camera position/orientation in 3d space
81     typedef struct Camera3D {
82         Vector3 position;       // Camera position
83         Vector3 target;         // Camera target it looks-at
84         Vector3 up;             // Camera up vector (rotation over its axis)
85         float fovy;             // Camera field-of-view apperture in Y (degrees) in perspective, used as near plane width in orthographic
86         int projection;         // Camera projection type: CAMERA_PERSPECTIVE or CAMERA_ORTHOGRAPHIC
87     } Camera3D;
88 
89     typedef Camera3D Camera;    // Camera type fallback, defaults to Camera3D
90 
91     // Camera projection
92     typedef enum {
93         CAMERA_PERSPECTIVE = 0, // Perspective projection
94         CAMERA_ORTHOGRAPHIC     // Orthographic projection
95     } CameraProjection;
96 
97     // Camera system modes
98     typedef enum {
99         CAMERA_CUSTOM = 0,      // Camera custom, controlled by user (UpdateCamera() does nothing)
100         CAMERA_FREE,            // Camera free mode
101         CAMERA_ORBITAL,         // Camera orbital, around target, zoom supported
102         CAMERA_FIRST_PERSON,    // Camera first person
103         CAMERA_THIRD_PERSON     // Camera third person
104     } CameraMode;
105 #endif
