429 #ifndef CAMERA_STANDALONE
430 // Update camera position for selected mode
431 // Camera mode: CAMERA_FREE, CAMERA_FIRST_PERSON, CAMERA_THIRD_PERSON, CAMERA_ORBITAL or CUSTOM
432 void UpdateCamera(Camera *camera, int mode)
433 {
434     Vector2 mousePositionDelta = GetMouseDelta();
435 
436     bool moveInWorldPlane = ((mode == CAMERA_FIRST_PERSON) || (mode == CAMERA_THIRD_PERSON));
437     bool rotateAroundTarget = ((mode == CAMERA_THIRD_PERSON) || (mode == CAMERA_ORBITAL));
438     bool lockView = ((mode == CAMERA_FIRST_PERSON) || (mode == CAMERA_THIRD_PERSON) || (mode == CAMERA_ORBITAL));
439     bool rotateUp = (mode == CAMERA_FREE);
440 
441     if (mode == CAMERA_ORBITAL)
442     {
443         // Orbital can just orbit
444         Matrix rotation = MatrixRotate(GetCameraUp(camera), CAMERA_ORBITAL_SPEED*GetFrameTime());
445         Vector3 view = Vector3Subtract(camera->position, camera->target);
446         view = Vector3Transform(view, rotation);
447         camera->position = Vector3Add(camera->target, view);
448     }
449     else
450     {
451         // Camera rotation
452         if (IsKeyDown(KEY_DOWN)) CameraPitch(camera, -CAMERA_ROTATION_SPEED, lockView, rotateAroundTarget, rotateUp);
453         if (IsKeyDown(KEY_UP)) CameraPitch(camera, CAMERA_ROTATION_SPEED, lockView, rotateAroundTarget, rotateUp);
454         if (IsKeyDown(KEY_RIGHT)) CameraYaw(camera, -CAMERA_ROTATION_SPEED, rotateAroundTarget);
455         if (IsKeyDown(KEY_LEFT)) CameraYaw(camera, CAMERA_ROTATION_SPEED, rotateAroundTarget);
456         if (IsKeyDown(KEY_Q)) CameraRoll(camera, -CAMERA_ROTATION_SPEED);
457         if (IsKeyDown(KEY_E)) CameraRoll(camera, CAMERA_ROTATION_SPEED);
458 
459         CameraYaw(camera, -mousePositionDelta.x*CAMERA_MOUSE_MOVE_SENSITIVITY, rotateAroundTarget);
460         CameraPitch(camera, -mousePositionDelta.y*CAMERA_MOUSE_MOVE_SENSITIVITY, lockView, rotateAroundTarget, rotateUp);
461 
462         // Camera movement
463         if (IsKeyDown(KEY_W)) CameraMoveForward(camera, CAMERA_MOVE_SPEED, moveInWorldPlane);
464         if (IsKeyDown(KEY_A)) CameraMoveRight(camera, -CAMERA_MOVE_SPEED, moveInWorldPlane);
465         if (IsKeyDown(KEY_S)) CameraMoveForward(camera, -CAMERA_MOVE_SPEED, moveInWorldPlane);
466         if (IsKeyDown(KEY_D)) CameraMoveRight(camera, CAMERA_MOVE_SPEED, moveInWorldPlane);
467         //if (IsKeyDown(KEY_SPACE)) CameraMoveUp(camera, CAMERA_MOVE_SPEED);
468         //if (IsKeyDown(KEY_LEFT_CONTROL)) CameraMoveUp(camera, -CAMERA_MOVE_SPEED);
469     }
470 
471     if ((mode == CAMERA_THIRD_PERSON) || (mode == CAMERA_ORBITAL))
472     {
473         // Zoom target distance
474         CameraMoveToTarget(camera, -GetMouseWheelMove());
475         if (IsKeyPressed(KEY_KP_SUBTRACT)) CameraMoveToTarget(camera, 2.0f);
476         if (IsKeyPressed(KEY_KP_ADD)) CameraMoveToTarget(camera, -2.0f);
477     }
478 }
479 #endif // !CAMERA_STANDALONE
480 
481 // Update camera movement, movement/rotation values should be provided by user
482 void UpdateCameraPro(Camera *camera, Vector3 movement, Vector3 rotation, float zoom)
483 {
484     // Required values
485     // movement.x - Move forward/backward
486     // movement.y - Move right/left
487     // movement.z - Move up/down
488     // rotation.x - yaw
489     // rotation.y - pitch
490     // rotation.z - roll
491     // zoom - Move towards target
492 
493     bool lockView = true;
494     bool rotateAroundTarget = false;
495     bool rotateUp = false;
496     bool moveInWorldPlane = true;
497 
498     // Camera rotation
499     CameraPitch(camera, -rotation.y*DEG2RAD, lockView, rotateAroundTarget, rotateUp);
500     CameraYaw(camera, -rotation.x*DEG2RAD, rotateAroundTarget);
501     CameraRoll(camera, rotation.z*DEG2RAD);
502 
503     // Camera movement
504     CameraMoveForward(camera, movement.x, moveInWorldPlane);
505     CameraMoveRight(camera, movement.y, moveInWorldPlane);
506     CameraMoveUp(camera, movement.z);
507 
508     // Zoom target distance
509     CameraMoveToTarget(camera, zoom);
510 }
511 
512 #endif // CAMERA_IMPLEMENTATION
