307 // Framebuffer configuration
308 //
309 // This describes buffers and their sizes.  It also contains
310 // a platform-specific ID used to map back to the backend API object.
311 //
312 // It is used to pass framebuffer parameters from shared code to the platform
313 // API and also to enumerate and select available framebuffer configs.
314 //
315 struct _GLFWfbconfig
316 {
317     int         redBits;
318     int         greenBits;
319     int         blueBits;
320     int         alphaBits;
321     int         depthBits;
322     int         stencilBits;
323     int         accumRedBits;
324     int         accumGreenBits;
325     int         accumBlueBits;
326     int         accumAlphaBits;
327     int         auxBuffers;
328     GLFWbool    stereo;
329     int         samples;
330     GLFWbool    sRGB;
331     GLFWbool    doublebuffer;
332     GLFWbool    transparent;
333     uintptr_t   handle;
334 };
