392 static inline void msf_lzw_reset(MsfStridedList * lzw, int tableSize, int stride) { MsfTimeFunc
393     memset(lzw->data, 0xFF, 4096 * stride * sizeof(int16_t));
394     lzw->len = tableSize + 2;
395     lzw->stride = stride;
396 }
