22     template<typename T>
23     GLM_FUNC_QUALIFIER T mask(T Bits)
24     {
25         return Bits >= static_cast<T>(sizeof(T) * 8) ? ~static_cast<T>(0) : (static_cast<T>(1) << Bits) - static_cast<T>(1);
26     }
