46     template<length_t L, typename T, qualifier Q, bool Aligned, bool EXEC>
47     struct compute_bitfieldBitCountStep
48     {
49         GLM_FUNC_QUALIFIER static vec<L, T, Q> call(vec<L, T, Q> const& v, T, T)
50         {
51             return v;
52         }
53     };
