64     template<typename genIUType, size_t Bits>
65     struct compute_findLSB
66     {
67         GLM_FUNC_QUALIFIER static int call(genIUType Value)
68         {
69             if(Value == 0)
70                 return -1;
71 
72             return glm::bitCount(~Value & (Value - static_cast<genIUType>(1)));
73         }
74     };
