102     template<length_t L, typename T, qualifier Q, bool EXEC = true>
103     struct compute_findMSB_step_vec
104     {
105         GLM_FUNC_QUALIFIER static vec<L, T, Q> call(vec<L, T, Q> const& x, T Shift)
106         {
107             return x | (x >> Shift);
108         }
