222     {
223         vec<L, detail::uint64, Q> Value64(vec<L, detail::uint64, Q>(x) * vec<L, detail::uint64, Q>(y));
224         msb = vec<L, uint, Q>(Value64 >> static_cast<detail::uint64>(32));
225         lsb = vec<L, uint, Q>(Value64);
