244     // bitfieldExtract
245     template<typename genIUType>
246     GLM_FUNC_QUALIFIER genIUType bitfieldExtract(genIUType Value, int Offset, int Bits)
247     {
248         return bitfieldExtract(vec<1, genIUType>(Value), Offset, Bits).x;
249     }
