467     template<typename T, qualifier Q>
468     GLM_FUNC_QUALIFIER typename mat<3, 3, T, Q>::col_type operator*(mat<3, 3, T, Q> const& m, typename mat<3, 3, T, Q>::row_type const& v)
469     {
470         return typename mat<3, 3, T, Q>::col_type(
471             m[0][0] * v.x + m[1][0] * v.y + m[2][0] * v.z,
472             m[0][1] * v.x + m[1][1] * v.y + m[2][1] * v.z,
473             m[0][2] * v.x + m[1][2] * v.y + m[2][2] * v.z);
474     }
