670     template<typename T, qualifier Q>
671     GLM_FUNC_QUALIFIER typename mat<4, 4, T, Q>::col_type operator/(mat<4, 4, T, Q> const& m, typename mat<4, 4, T, Q>::row_type const& v)
672     {
673         return inverse(m) * v;
674     }
