676     template<typename T, qualifier Q>
677     GLM_FUNC_QUALIFIER typename mat<4, 4, T, Q>::row_type operator/(typename mat<4, 4, T, Q>::col_type const& v, mat<4, 4, T, Q> const& m)
678     {
679         return v * inverse(m);
680     }
