682     template<typename T, qualifier Q>
683     GLM_FUNC_QUALIFIER mat<4, 4, T, Q> operator/(mat<4, 4, T, Q> const& m1, mat<4, 4, T, Q> const& m2)
684     {
685         mat<4, 4, T, Q> m1_copy(m1);
686         return m1_copy /= m2;
