5     template<typename T, qualifier Q>
6     GLM_FUNC_QUALIFIER mat<3, 3, T, Q> affineInverse(mat<3, 3, T, Q> const& m)
7     {
8         mat<2, 2, T, Q> const Inv(inverse(mat<2, 2, T, Q>(m)));
9 
10         return mat<3, 3, T, Q>(
11             vec<3, T, Q>(Inv[0], static_cast<T>(0)),
12             vec<3, T, Q>(Inv[1], static_cast<T>(0)),
13             vec<3, T, Q>(-Inv * vec<2, T, Q>(m[2]), static_cast<T>(1)));
14     }
