16     template<typename T, qualifier Q>
17     GLM_FUNC_QUALIFIER mat<4, 4, T, Q> affineInverse(mat<4, 4, T, Q> const& m)
18     {
19         mat<3, 3, T, Q> const Inv(inverse(mat<3, 3, T, Q>(m)));
20 
21         return mat<4, 4, T, Q>(
22             vec<4, T, Q>(Inv[0], static_cast<T>(0)),
23             vec<4, T, Q>(Inv[1], static_cast<T>(0)),
24             vec<4, T, Q>(Inv[2], static_cast<T>(0)),
25             vec<4, T, Q>(-Inv * vec<3, T, Q>(m[3]), static_cast<T>(1)));
26     }
