28     template<typename T, qualifier Q>
29     GLM_FUNC_QUALIFIER mat<2, 2, T, Q> inverseTranspose(mat<2, 2, T, Q> const& m)
30     {
31         T Determinant = m[0][0] * m[1][1] - m[1][0] * m[0][1];
32 
33         mat<2, 2, T, Q> Inverse(
34             + m[1][1] / Determinant,
35             - m[0][1] / Determinant,
36             - m[1][0] / Determinant,
37             + m[0][0] / Determinant);
38 
39         return Inverse;
40     }
