42     template<typename T, qualifier Q>
43     GLM_FUNC_QUALIFIER mat<3, 3, T, Q> inverseTranspose(mat<3, 3, T, Q> const& m)
44     {
45         T Determinant =
46             + m[0][0] * (m[1][1] * m[2][2] - m[1][2] * m[2][1])
47             - m[0][1] * (m[1][0] * m[2][2] - m[1][2] * m[2][0])
48             + m[0][2] * (m[1][0] * m[2][1] - m[1][1] * m[2][0]);
49 
50         mat<3, 3, T, Q> Inverse;
51         Inverse[0][0] = + (m[1][1] * m[2][2] - m[2][1] * m[1][2]);
52         Inverse[0][1] = - (m[1][0] * m[2][2] - m[2][0] * m[1][2]);
53         Inverse[0][2] = + (m[1][0] * m[2][1] - m[2][0] * m[1][1]);
54         Inverse[1][0] = - (m[0][1] * m[2][2] - m[2][1] * m[0][2]);
55         Inverse[1][1] = + (m[0][0] * m[2][2] - m[2][0] * m[0][2]);
56         Inverse[1][2] = - (m[0][0] * m[2][1] - m[2][0] * m[0][1]);
57         Inverse[2][0] = + (m[0][1] * m[1][2] - m[1][1] * m[0][2]);
58         Inverse[2][1] = - (m[0][0] * m[1][2] - m[1][0] * m[0][2]);
59         Inverse[2][2] = + (m[0][0] * m[1][1] - m[1][0] * m[0][1]);
60         Inverse /= Determinant;
61 
62         return Inverse;
63     }
