112     GLM_FUNC_QUALIFIER glm::uint half2float(glm::uint h)
113     {
114         return ((h & 0x8000) << 16) | ((( h & 0x7c00) + 0x1C000) << 13) | ((h & 0x03FF) << 13);
115     }
