117     GLM_FUNC_QUALIFIER glm::uint floatTo11bit(float x)
118     {
119         if(x == 0.0f)
120             return 0u;
121         else if(glm::isnan(x))
122             return ~0u;
123         else if(glm::isinf(x))
124             return 0x1Fu << 6u;
125 
126         uint Pack = 0u;
127         memcpy(&Pack, &x, sizeof(Pack));
128         return float2packed11(Pack);
129     }
