131     GLM_FUNC_QUALIFIER float packed11bitToFloat(glm::uint x)
132     {
133         if(x == 0)
134             return 0.0f;
135         else if(x == ((1 << 11) - 1))
136             return ~0;//NaN
137         else if(x == (0x1f << 6))
138             return ~0;//Inf
139 
140         uint Result = packed11ToFloat(x);
141 
142         float Temp = 0;
143         memcpy(&Temp, &Result, sizeof(Temp));
144         return Temp;
145     }
