599     GLM_FUNC_QUALIFIER uint32 packF2x11_1x10(vec3 const& v)
600     {
601         return
602             ((detail::floatTo11bit(v.x) & ((1 << 11) - 1)) <<  0) |
603             ((detail::floatTo11bit(v.y) & ((1 << 11) - 1)) << 11) |
604             ((detail::floatTo10bit(v.z) & ((1 << 10) - 1)) << 22);
605     }
