102     directory_entry(directory_entry&&) noexcept = default;
103 
104     explicit
105     directory_entry(const filesystem::path& __p)
106     : _M_path(__p)
107     { refresh(); }
108 
109     directory_entry(const filesystem::path& __p, error_code& __ec)
110     : _M_path(__p)
111     {
112       refresh(__ec);
113       if (__ec)
114     _M_path.clear();
115     }
116 
117     ~directory_entry() = default;
118 
119     // modifiers
