1914   // 20.7.2.2.8 shared_ptr specialized algorithms.
1915   template<typename _Tp, _Lock_policy _Lp>
1916     inline void
1917     swap(__shared_ptr<_Tp, _Lp>& __a, __shared_ptr<_Tp, _Lp>& __b) noexcept
1918     { __a.swap(__b); }
