480   template<typename _InternT, typename _ExternT>
481     int 
482     codecvt<_InternT, _ExternT, encoding_state>::
483     do_encoding() const throw()
484     {
485       int __ret = 0;
486       if (sizeof(_ExternT) <= sizeof(_InternT))
487     __ret = sizeof(_InternT) / sizeof(_ExternT);
488       return __ret; 
489     }
