45 PB_DS_CLASS_T_DEC
46 void
47 PB_DS_CLASS_C_DEC::
48 trace() const
49 {
50   std::cerr << std::endl;
51   if (m_p_head->m_p_parent == 0)
52     return;
53   trace_node(m_p_head->m_p_parent, 0);
54   std::cerr << std::endl;
55 }
56 
57 PB_DS_CLASS_T_DEC
58 void
59 PB_DS_CLASS_C_DEC::
60 trace_node(node_const_pointer p_nd, size_type level)
61 {
62   for (size_type i = 0; i < level; ++i)
63     std::cerr << ' ';
64   std::cerr << p_nd << " ";
65   std::cerr << ((p_nd->m_type == pat_trie_leaf_node_type) ? "l " : "i ");
66 
67   trace_node_metadata(p_nd, type_to_type<typename node::metadata_type>());
68   typename access_traits::const_iterator el_it = pref_begin(p_nd);
69   while (el_it != pref_end(p_nd))
70     {
71       std::cerr <<* el_it;
72       ++el_it;
73     }
74 
75   if (p_nd->m_type == pat_trie_leaf_node_type)
76     {
77       std::cerr << std::endl;
78       return;
79     }
80 
81   inode_const_pointer p_internal = static_cast<inode_const_pointer>(p_nd);
82 
83   std::cerr << " " <<
84     static_cast<unsigned long>(p_internal->get_e_ind()) << std::endl;
85 
86   const size_type num_children = std::distance(p_internal->begin(),
87                            p_internal->end());
88 
89   for (size_type child_i = 0; child_i < num_children; ++child_i)
90     {
91       typename inode::const_iterator child_it = p_internal->begin();
92       std::advance(child_it, num_children - child_i - 1);
93       trace_node(*child_it, level + 1);
94     }
95 }
96 
97 PB_DS_CLASS_T_DEC
98 template<typename Metadata_>
99 void
100 PB_DS_CLASS_C_DEC::
101 trace_node_metadata(node_const_pointer p_nd, type_to_type<Metadata_>)
102 {
103   std::cerr << "(" << static_cast<unsigned long>(p_nd->get_metadata()) << ") ";
104 }
