183 // X11-specific per-window data
184 //
185 typedef struct _GLFWwindowX11
186 {
187     Colormap        colormap;
188     Window          handle;
189     Window          parent;
190     XIC             ic;
191 
192     GLFWbool        overrideRedirect;
193     GLFWbool        iconified;
194     GLFWbool        maximized;
195 
196     // Whether the visual supports framebuffer transparency
197     GLFWbool        transparent;
198 
199     // Cached position and size used to filter out duplicate events
200     int             width, height;
201     int             xpos, ypos;
202 
203     // The last received cursor position, regardless of source
204     int             lastCursorPosX, lastCursorPosY;
205     // The last position the cursor was warped to by GLFW
206     int             warpCursorPosX, warpCursorPosY;
207 
208     // The time of the last KeyPress event
209     Time            lastKeyTime;
210 
211 } _GLFWwindowX11;
