293 // Calculate distance between two vectors
294 RMAPI float Vector2Distance(Vector2 v1, Vector2 v2)
295 {
296     float result = sqrtf((v1.x - v2.x)*(v1.x - v2.x) + (v1.y - v2.y)*(v1.y - v2.y));
297 
298     return result;
299 }
