309 // Calculate angle between two vectors
310 // NOTE: Angle is calculated from origin point (0, 0)
311 RMAPI float Vector2Angle(Vector2 v1, Vector2 v2)
312 {
313     float result = atan2f(v2.y - v1.y, v2.x - v1.x);
314 
315     return result;
316 }
