318 // Calculate angle defined by a two vectors line
319 // NOTE: Parameters need to be normalized
320 // Current implementation should be aligned with glm::angle
321 RMAPI float Vector2LineAngle(Vector2 start, Vector2 end)
322 {
323     float result = 0.0f;
324 
325     float dot = start.x*end.x + start.y*end.y;      // Dot product
326 
327     float dotClamp = (dot < -1.0f)? -1.0f : dot;    // Clamp
328     if (dotClamp > 1.0f) dotClamp = 1.0f;
329 
330     result = acosf(dotClamp);
331 
332     // Alternative implementation, more costly
333     //float v1Length = sqrtf((start.x*start.x) + (start.y*start.y));
334     //float v2Length = sqrtf((end.x*end.x) + (end.y*end.y));
335     //float result = -acosf((start.x*end.x + start.y*end.y)/(v1Length*v2Length));
336 
337     return result;
338 }
