372 // Normalize provided vector
373 RMAPI Vector2 Vector2Normalize(Vector2 v)
374 {
375     Vector2 result = { 0 };
376     float length = sqrtf((v.x*v.x) + (v.y*v.y));
377 
378     if (length > 0)
379     {
380         float ilength = 1.0f/length;
381         result.x = v.x*ilength;
382         result.y = v.y*ilength;
383     }
384 
385     return result;
386 }
387 
388 // Transforms a Vector2 by a given Matrix
389 RMAPI Vector2 Vector2Transform(Vector2 v, Matrix mat)
390 {
391     Vector2 result = { 0 };
392 
393     float x = v.x;
394     float y = v.y;
395     float z = 0;
396 
397     result.x = mat.m0*x + mat.m4*y + mat.m8*z + mat.m12;
398     result.y = mat.m1*x + mat.m5*y + mat.m9*z + mat.m13;
399 
400     return result;
401 }
402 
403 // Calculate linear interpolation between two vectors
404 RMAPI Vector2 Vector2Lerp(Vector2 v1, Vector2 v2, float amount)
405 {
406     Vector2 result = { 0 };
407 
408     result.x = v1.x + amount*(v2.x - v1.x);
409     result.y = v1.y + amount*(v2.y - v1.y);
410 
411     return result;
412 }
413 
414 // Calculate reflected vector to normal
415 RMAPI Vector2 Vector2Reflect(Vector2 v, Vector2 normal)
416 {
417     Vector2 result = { 0 };
418 
419     float dotProduct = (v.x*normal.x + v.y*normal.y); // Dot product
420 
421     result.x = v.x - (2.0f*normal.x)*dotProduct;
422     result.y = v.y - (2.0f*normal.y)*dotProduct;
423 
424     return result;
425 }
426 
427 // Rotate vector by angle
428 RMAPI Vector2 Vector2Rotate(Vector2 v, float angle)
429 {
430     Vector2 result = { 0 };
431 
432     float cosres = cosf(angle);
433     float sinres = sinf(angle);
434 
435     result.x = v.x*cosres - v.y*sinres;
436     result.y = v.x*sinres + v.y*cosres;
437 
438     return result;
439 }
440 
441 // Move Vector towards target
442 RMAPI Vector2 Vector2MoveTowards(Vector2 v, Vector2 target, float maxDistance)
443 {
444     Vector2 result = { 0 };
445 
446     float dx = target.x - v.x;
447     float dy = target.y - v.y;
448     float value = (dx*dx) + (dy*dy);
449 
450     if ((value == 0) || ((maxDistance >= 0) && (value <= maxDistance*maxDistance))) return target;
451 
452     float dist = sqrtf(value);
453 
454     result.x = v.x + dx/dist*maxDistance;
455     result.y = v.y + dy/dist*maxDistance;
456 
457     return result;
458 }
459 
460 // Invert the given vector
461 RMAPI Vector2 Vector2Invert(Vector2 v)
462 {
463     Vector2 result = { 1.0f/v.x, 1.0f/v.y };
464 
465     return result;
466 }
