468 // Clamp the components of the vector between
469 // min and max values specified by the given vectors
470 RMAPI Vector2 Vector2Clamp(Vector2 v, Vector2 min, Vector2 max)
471 {
472     Vector2 result = { 0 };
473 
474     result.x = fminf(max.x, fmaxf(min.x, v.x));
475     result.y = fminf(max.y, fmaxf(min.y, v.y));
476 
477     return result;
478 }
479 
480 // Clamp the magnitude of the vector between two min and max values
481 RMAPI Vector2 Vector2ClampValue(Vector2 v, float min, float max)
482 {
483     Vector2 result = v;
484 
485     float length = (v.x*v.x) + (v.y*v.y);
486     if (length > 0.0f)
487     {
488         length = sqrtf(length);
489 
490         if (length < min)
491         {
492             float scale = min/length;
493             result.x = v.x*scale;
494             result.y = v.y*scale;
495         }
496         else if (length > max)
497         {
498             float scale = max/length;
499             result.x = v.x*scale;
500             result.y = v.y*scale;
501         }
502     }
503 
504     return result;
505 }
506 
507 // Check whether two given vectors are almost equal
508 RMAPI int Vector2Equals(Vector2 p, Vector2 q)
509 {
510     int result = ((fabsf(p.x - q.x)) <= (EPSILON*fmaxf(1.0f, fmaxf(fabsf(p.x), fabsf(q.x))))) &&
511                   ((fabsf(p.y - q.y)) <= (EPSILON*fmaxf(1.0f, fmaxf(fabsf(p.y), fabsf(q.y)))));
512 
513     return result;
514 }
