584 // Calculate two vectors cross product
585 RMAPI Vector3 Vector3CrossProduct(Vector3 v1, Vector3 v2)
586 {
587     Vector3 result = { v1.y*v2.z - v1.z*v2.y, v1.z*v2.x - v1.x*v2.z, v1.x*v2.y - v1.y*v2.x };
588 
589     return result;
590 }
591 
592 // Calculate one vector perpendicular vector
593 RMAPI Vector3 Vector3Perpendicular(Vector3 v)
594 {
595     Vector3 result = { 0 };
596 
597     float min = (float) fabs(v.x);
598     Vector3 cardinalAxis = {1.0f, 0.0f, 0.0f};
599 
600     if (fabsf(v.y) < min)
601     {
602         min = (float) fabs(v.y);
603         Vector3 tmp = {0.0f, 1.0f, 0.0f};
604         cardinalAxis = tmp;
605     }
606 
607     if (fabsf(v.z) < min)
608     {
609         Vector3 tmp = {0.0f, 0.0f, 1.0f};
610         cardinalAxis = tmp;
611     }
612 
613     // Cross product between vectors
614     result.x = v.y*cardinalAxis.z - v.z*cardinalAxis.y;
615     result.y = v.z*cardinalAxis.x - v.x*cardinalAxis.z;
616     result.z = v.x*cardinalAxis.y - v.y*cardinalAxis.x;
617 
618     return result;
619 }
