645 // Calculate distance between two vectors
646 RMAPI float Vector3Distance(Vector3 v1, Vector3 v2)
647 {
648     float result = 0.0f;
649 
650     float dx = v2.x - v1.x;
651     float dy = v2.y - v1.y;
652     float dz = v2.z - v1.z;
653     result = sqrtf(dx*dx + dy*dy + dz*dz);
654 
655     return result;
656 }
657 
658 // Calculate square distance between two vectors
659 RMAPI float Vector3DistanceSqr(Vector3 v1, Vector3 v2)
660 {
661     float result = 0.0f;
662 
663     float dx = v2.x - v1.x;
664     float dy = v2.y - v1.y;
665     float dz = v2.z - v1.z;
666     result = dx*dx + dy*dy + dz*dz;
667 
668     return result;
669 }
670 
671 // Calculate angle between two vectors
672 RMAPI float Vector3Angle(Vector3 v1, Vector3 v2)
673 {
674     float result = 0.0f;
675 
676     Vector3 cross = { v1.y*v2.z - v1.z*v2.y, v1.z*v2.x - v1.x*v2.z, v1.x*v2.y - v1.y*v2.x };
677     float len = sqrtf(cross.x*cross.x + cross.y*cross.y + cross.z*cross.z);
678     float dot = (v1.x*v2.x + v1.y*v2.y + v1.z*v2.z);
679     result = atan2f(len, dot);
680 
681     return result;
682 }
